
public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int  time =20;
		if (time<10) {
			System.out.println("good day");
		}
			else if(time<21) {
				System.out.println("good evening");
				}
		
				else {
					System.out.println("good night");
			
		}


	}}

