package string;

public class Equalsignorecase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="priyanshi";  
		String s2="PRIYANSHI";
		
		
		
		String s3="priyanshi";  
		String s4="priyanshi";
		
		
		
		System.out.println(s1.equalsIgnoreCase(s2));
		System.out.println(s3.equalsIgnoreCase(s4));
	}

}
